              >
                <SelectTrigger className="w-full">
                  <SelectValue placeholder="Kurs auswählen" />
                </SelectTrigger>
                <SelectContent>
                  {courses.map((c) => (
                    <SelectItem key={c.record_id} value={c.record_id}>
                      {c.fields.title}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label htmlFor="registration_date">Anmeldedatum *</Label>
              <Input
